  1→/**
  2→ * Merge check orchestrator
  3→ *
  4→ * Coordinates all merge-check modules and resolves branches
  5→ * from run logs and git state.
  6→ */
  7→
  8→import * as fs from "fs";
  9→import * as path from "path";
 10→import * as os from "os";
 11→import { spawnSync } from "child_process";
 12→import {
 13→  RunLogSchema,
 14→  type RunLog,
 15→  LOG_PATHS,
 16→} from "../workflow/run-log-schema.js";
 17→import { getGitDiffStats } from "../workflow/git-diff-utils.js";
 18→import type {
 19→  BranchInfo,
 20→  CheckResult,
 21→  MergeCommandOptions,
 22→  MergeReport,
 23→} from "./types.js";
 24→import { DEFAULT_MIRROR_PAIRS } from "./types.js";
 25→import { runCombinedBranchTest } from "./combined-branch-test.js";
 26→import { runMirroringCheck } from "./mirroring-check.js";
 27→import { runOverlapDetection } from "./overlap-detection.js";
 28→import { runResidualPatternScan } from "./residual-pattern-scan.js";
 29→import {
 30→  buildReport,
 31→  formatReportMarkdown,
 32→  formatBranchReportMarkdown,
 33→  postReportToGitHub,
 34→} from "./report.js";
 35→
 36→/**
 37→ * Resolve log directory path
 38→ */
 39→function resolveLogDir(customPath?: string): string {
 40→  if (customPath) {
 41→    return customPath.replace("~", os.homedir());
 42→  }
 43→  const projectPath = LOG_PATHS.project;
 44→  if (fs.existsSync(projectPath)) {
 45→    return projectPath;
 46→  }
 47→  const userPath = LOG_PATHS.user.replace("~", os.homedir());
 48→  if (fs.existsSync(userPath)) {
 49→    return userPath;
 50→  }
 51→  return projectPath;
 52→}
 53→
 54→/**
 55→ * Find the most recent run log file
 56→ */
 57→export function findMostRecentLog(logDir: string): RunLog | null {
 58→  if (!fs.existsSync(logDir)) {
 59→    return null;
 60→  }
 61→
 62→  const files = fs
 63→    .readdirSync(logDir)
 64→    .filter((f) => f.startsWith("run-") && f.endsWith(".json"))
 65→    .sort()
 66→    .reverse();
 67→
 68→  if (files.length === 0) {
 69→    return null;
 70→  }
 71→
 72→  const content = fs.readFileSync(path.join(logDir, files[0]), "utf-8");
 73→  const parsed = RunLogSchema.safeParse(JSON.parse(content));
 74→  if (!parsed.success) {
 75→    return null;
 76→  }
 77→
 78→  return parsed.data;
 79→}
 80→
 81→/**
 82→ * Resolve branches from issue numbers.
 83→ *
 84→ * Uses git worktree list and remote branch patterns to find
 85→ * the feature branches for each issue.
 86→ */
 87→export function resolveBranches(
 88→  issueNumbers: number[],
 89→  repoRoot: string,
 90→  runLog?: RunLog | null,
 91→): BranchInfo[] {
 92→  const branches: BranchInfo[] = [];
 93→
 94→  // Get remote branches matching feature pattern
 95→  const branchResult = spawnSync(
 96→    "git",
 97→    ["-C", repoRoot, "branch", "-r", "--list", "origin/feature/*"],
 98→    { stdio: "pipe", encoding: "utf-8" },
 99→  );
100→
101→  const remoteBranches = branchResult.stdout
102→    ? branchResult.stdout
103→        .split("\n")
104→        .map((b) => b.trim().replace("origin/", ""))
105→        .filter(Boolean)
106→    : [];
107→
108→  // Also check worktrees for local-only branches
109→  const worktreeResult = spawnSync(
110→    "git",
111→    ["-C", repoRoot, "worktree", "list", "--porcelain"],
112→    { stdio: "pipe", encoding: "utf-8" },
113→  );
114→
115→  const worktreePaths = new Map<string, string>();
116→  let currentPath = "";
117→  for (const line of (worktreeResult.stdout ?? "").split("\n")) {
118→    if (line.startsWith("worktree ")) {
119→      currentPath = line.slice(9);
120→    } else if (line.startsWith("branch refs/heads/")) {
121→      const branch = line.slice(18);
122→      worktreePaths.set(branch, currentPath);
123→    }
124→  }
125→
126→  // Get run log issue info for titles
127→  const issueInfo = new Map<number, { title: string; prNumber?: number }>();
128→  if (runLog) {
129→    for (const issue of runLog.issues) {
130→      issueInfo.set(issue.issueNumber, {
131→        title: issue.title,
132→        prNumber: issue.prNumber,
133→      });
134→    }
135→  }
136→
137→  for (const issueNumber of issueNumbers) {
138→    // Find the branch for this issue
139→    const branchPattern = new RegExp(`^feature/${issueNumber}-`);
140→    const branch =
141→      remoteBranches.find((b) => branchPattern.test(b)) ??
142→      Array.from(worktreePaths.keys()).find((b) => branchPattern.test(b));
143→
144→    if (!branch) {
145→      console.error(`No branch found for issue #${issueNumber}`);
146→      continue;
147→    }
148→
149→    // Get modified files from the branch
150→    const worktreePath = worktreePaths.get(branch);
151→    let filesModified: string[] = [];
152→
153→    if (worktreePath) {
154→      // Use worktree for diff
155→      const diffStats = getGitDiffStats(worktreePath);
156→      filesModified = diffStats.filesModified;
157→    } else {
158→      // Use remote branch diff
159→      const diffResult = spawnSync(
160→        "git",
161→        [
162→          "-C",
163→          repoRoot,
164→          "diff",
165→          "--name-only",
166→          `origin/main...origin/${branch}`,
167→        ],
168→        { stdio: "pipe", encoding: "utf-8" },
169→      );
170→      filesModified = diffResult.stdout
171→        ? diffResult.stdout.split("\n").filter(Boolean)
172→        : [];
173→    }
174→
175→    const info = issueInfo.get(issueNumber);
176→    const title =
177→      info?.title ?? fetchIssueTitle(issueNumber) ?? `Issue #${issueNumber}`;
178→
179→    branches.push({
180→      issueNumber,
181→      title,
182→      branch,
183→      worktreePath,
184→      prNumber: info?.prNumber,
185→      filesModified,
186→    });
187→  }
188→
189→  return branches;
190→}
191→
