  1→/**
  2→ * Combined branch testing (AC-1)
  3→ *
  4→ * Creates a temporary branch merging all feature branches from a run batch,
  5→ * runs npm test && npm run build on the combined state, and reports results.
  6→ */
  7→
  8→import { spawnSync } from "child_process";
  9→import type {
 10→  BranchInfo,
 11→  CheckResult,
 12→  BranchCheckResult,
 13→  CheckFinding,
 14→} from "./types.js";
 15→
 16→/**
 17→ * Result from merging a branch into the temp branch
 18→ */
 19→interface MergeAttempt {
 20→  issueNumber: number;
 21→  branch: string;
 22→  success: boolean;
 23→  conflictFiles?: string[];
 24→  error?: string;
 25→}
 26→
 27→/**
 28→ * Run a git command and return the result
 29→ */
 30→function git(
 31→  args: string[],
 32→  cwd?: string,
 33→): { ok: boolean; stdout: string; stderr: string } {
 34→  const result = spawnSync("git", args, {
 35→    cwd,
 36→    stdio: "pipe",
 37→    encoding: "utf-8",
 38→  });
 39→  return {
 40→    ok: result.status === 0,
 41→    stdout: result.stdout?.trim() ?? "",
 42→    stderr: result.stderr?.trim() ?? "",
 43→  };
 44→}
 45→
 46→/**
 47→ * Run npm command and return result
 48→ */
 49→function npm(
 50→  args: string[],
 51→  cwd?: string,
 52→): { ok: boolean; stdout: string; stderr: string } {
 53→  const result = spawnSync("npm", args, {
 54→    cwd,
 55→    stdio: "pipe",
 56→    encoding: "utf-8",
 57→    timeout: 120_000, // 2 min timeout for test/build
 58→  });
 59→  return {
 60→    ok: result.status === 0,
 61→    stdout: result.stdout?.trim() ?? "",
 62→    stderr: result.stderr?.trim() ?? "",
 63→  };
 64→}
 65→
 66→/**
 67→ * Create temp branch, merge all feature branches, run tests and build.
 68→ *
 69→ * @param branches - Feature branches to merge
 70→ * @param repoRoot - Path to the git repository root
 71→ * @returns CheckResult with combined test findings
 72→ */
 73→export function runCombinedBranchTest(
 74→  branches: BranchInfo[],
 75→  repoRoot: string,
 76→): CheckResult {
 77→  const startTime = Date.now();
 78→  const tempBranch = `merge-check/temp-${Date.now()}`;
 79→  const branchResults: BranchCheckResult[] = [];
 80→  const batchFindings: CheckFinding[] = [];
 81→  const mergeAttempts: MergeAttempt[] = [];
 82→
 83→  try {
 84→    // Fetch latest from remote
 85→    git(["fetch", "origin"], repoRoot);
 86→
 87→    // Create temp branch from main
 88→    const createResult = git(
 89→      ["checkout", "-b", tempBranch, "origin/main"],
 90→      repoRoot,
 91→    );
 92→    if (!createResult.ok) {
 93→      batchFindings.push({
 94→        check: "combined-branch-test",
 95→        severity: "error",
 96→        message: `Failed to create temp branch: ${createResult.stderr}`,
 97→      });
 98→      return buildResult(branchResults, batchFindings, startTime);
 99→    }
100→
101→    // Merge each feature branch
102→    for (const branch of branches) {
103→      const mergeResult = git(
104→        ["merge", "--no-ff", "--no-edit", `origin/${branch.branch}`],
105→        repoRoot,
106→      );
107→
108→      if (mergeResult.ok) {
109→        mergeAttempts.push({
110→          issueNumber: branch.issueNumber,
111→          branch: branch.branch,
112→          success: true,
113→        });
114→        branchResults.push({
