  1→/**
  2→ * Residual pattern detection (AC-3)
  3→ *
  4→ * Extracts removed/replaced patterns from each PR's diff,
  5→ * generates literal search strings, and greps the codebase
  6→ * for remaining instances (excluding already-modified files).
  7→ */
  8→
  9→import { spawnSync } from "child_process";
 10→import type {
 11→  BranchInfo,
 12→  CheckResult,
 13→  BranchCheckResult,
 14→  CheckFinding,
 15→  ExtractedPattern,
 16→  ResidualMatch,
 17→} from "./types.js";
 18→
 19→/**
 20→ * Minimum pattern length to avoid false positives from trivial removals
 21→ */
 22→const MIN_PATTERN_LENGTH = 8;
 23→
 24→/**
 25→ * Maximum number of patterns to scan per branch (avoid explosion)
 26→ */
 27→const MAX_PATTERNS_PER_BRANCH = 50;
 28→
 29→/**
 30→ * Lines that should be ignored when extracting patterns
 31→ * (import statements, blank lines, comments, etc.)
 32→ */
 33→const IGNORE_PATTERNS = [
 34→  /^\s*$/,
 35→  /^\s*\/\//,
 36→  /^\s*\/?\*/, // Single-line comment or multi-line comment line
 37→  /^\s*import\s/,
 38→  /^\s*export\s/,
 39→  /^\s*\{?\s*\}?\s*$/,
 40→  /^\s*\)\s*;?\s*$/,
 41→  /^\s*\]\s*;?\s*$/,
 42→];
 43→
 44→/**
 45→ * Extract removed patterns from a git diff for a branch.
 46→ *
 47→ * Looks at lines removed (prefixed with -) in the diff and extracts
 48→ * significant literal strings that might indicate incomplete migration.
 49→ */
 50→export function extractPatternsFromDiff(
 51→  branch: BranchInfo,
 52→  repoRoot: string,
 53→): ExtractedPattern[] {
 54→  const diffResult = spawnSync(
 55→    "git",
 56→    [
 57→      "-C",
 58→      repoRoot,
 59→      "diff",
 60→      `origin/main...origin/${branch.branch}`,
 61→      "--unified=0",
 62→    ],
 63→    { stdio: "pipe", encoding: "utf-8" },
 64→  );
 65→
 66→  if (diffResult.status !== 0 || !diffResult.stdout) {
 67→    return [];
 68→  }
 69→
 70→  const patterns: ExtractedPattern[] = [];
 71→  const seenPatterns = new Set<string>();
 72→  let currentFile = "";
 73→
 74→  for (const line of diffResult.stdout.split("\n")) {
 75→    // Track current file from diff header
 76→    if (line.startsWith("--- a/")) {
 77→      currentFile = line.slice(6);
 78→      continue;
 79→    }
 80→
 81→    // Only look at removed lines (not additions)
 82→    if (!line.startsWith("-") || line.startsWith("---")) {
 83→      continue;
 84→    }
 85→
 86→    const content = line.slice(1).trim();
 87→
 88→    // Skip trivial lines
 89→    if (content.length < MIN_PATTERN_LENGTH) continue;
 90→    if (IGNORE_PATTERNS.some((p) => p.test(content))) continue;
 91→
 92→    // Deduplicate
 93→    if (seenPatterns.has(content)) continue;
 94→    seenPatterns.add(content);
 95→
 96→    patterns.push({
 97→      pattern: content,
 98→      sourceFile: currentFile,
 99→      issueNumber: branch.issueNumber,
100→    });
101→
102→    if (patterns.length >= MAX_PATTERNS_PER_BRANCH) break;
103→  }
104→
105→  return patterns;
106→}
107→
108→/**
109→ * Search the codebase for remaining instances of extracted patterns.
110→ *
111→ * Uses git grep for literal string matching. Excludes files that were
112→ * already modified by the branch (they've already been addressed).
113→ */
114→export function findResiduals(
115→  patterns: ExtractedPattern[],
116→  excludeFiles: string[],
117→  repoRoot: string,
118→): ResidualMatch[] {
119→  const matches: ResidualMatch[] = [];
120→  const excludeSet = new Set(excludeFiles);
121→
122→  for (const pattern of patterns) {
123→    // Use git grep for literal search (fast, respects .gitignore)
124→    const grepResult = spawnSync(
125→      "git",
126→      [
127→        "-C",
128→        repoRoot,
129→        "grep",
130→        "-n",
131→        "--fixed-strings",
132→        pattern.pattern,
133→        "--",
134→        "*.ts",
135→        "*.tsx",
136→        "*.js",
137→        "*.jsx",
138→        "*.md",
139→        "*.sh",
140→      ],
141→      { stdio: "pipe", encoding: "utf-8", timeout: 10_000 },
142→    );
143→
144→    if (grepResult.status !== 0 || !grepResult.stdout) {
145→      continue;
146→    }
147→
148→    for (const line of grepResult.stdout.split("\n")) {
149→      if (!line.trim()) continue;
150→
151→      // Format: file:line:content
152→      const colonIdx = line.indexOf(":");
153→      if (colonIdx === -1) continue;
154→
155→      const file = line.slice(0, colonIdx);
156→      const rest = line.slice(colonIdx + 1);
157→      const secondColon = rest.indexOf(":");
158→      const lineNum =
159→        secondColon > -1 ? parseInt(rest.slice(0, secondColon), 10) : 0;
160→      const content =
161→        secondColon > -1 ? rest.slice(secondColon + 1).trim() : rest.trim();
162→
163→      // Skip files that were already modified by this branch
164→      if (excludeSet.has(file)) continue;
165→
166→      // Skip test files and node_modules
167→      if (
168→        file.includes("node_modules/") ||
169→        file.includes(".test.") ||
170→        file.includes(".spec.")
171→      ) {
172→        continue;
173→      }
174→
175→      matches.push({
176→        pattern: pattern.pattern,
177→        file,
178→        line: lineNum,
179→        content: content.slice(0, 200), // Truncate long lines
180→        issueNumber: pattern.issueNumber,
181→      });
182→    }
183→  }
184→
185→  return matches;
186→}
187→
188→/**
189→ * Run residual pattern scan across all branches.
190→ */
191→export function runResidualPatternScan(
192→  branches: BranchInfo[],
193→  repoRoot: string,
194→): CheckResult {
195→  const startTime = Date.now();
196→  const branchResults: BranchCheckResult[] = [];
197→  const batchFindings: CheckFinding[] = [];
198→
199→  for (const branch of branches) {
200→    const findings: CheckFinding[] = [];
201→
202→    // Extract patterns from this branch's diff
203→    const patterns = extractPatternsFromDiff(branch, repoRoot);
204→
205→    if (patterns.length === 0) {
206→      branchResults.push({
207→        issueNumber: branch.issueNumber,
208→        verdict: "PASS",
209→        findings: [
210→          {
211→            check: "residual-pattern-scan",
212→            severity: "info",
213→            message: "No significant patterns extracted from diff",
214→            issueNumber: branch.issueNumber,
215→          },
216→        ],
217→      });
218→      continue;
219→    }
220→
221→    // Search for residuals, excluding files already modified
222→    const residuals = findResiduals(patterns, branch.filesModified, repoRoot);
223→
224→    if (residuals.length === 0) {
225→      branchResults.push({
226→        issueNumber: branch.issueNumber,
227→        verdict: "PASS",
