  1→/**
  2→ * Merge readiness report generation (AC-5)
  3→ *
  4→ * Produces structured Markdown report with per-PR verdicts and
  5→ * batch-level verdict. Optionally posts to GitHub as PR comment.
  6→ */
  7→
  8→import { spawnSync } from "child_process";
  9→import type {
 10→  MergeReport,
 11→  CheckResult,
 12→  CheckVerdict,
 13→  BatchVerdict,
 14→  BranchInfo,
 15→  CheckFinding,
 16→} from "./types.js";
 17→
 18→/**
 19→ * Compute per-issue verdicts from check results
 20→ */
 21→export function computeIssueVerdicts(
 22→  branches: BranchInfo[],
 23→  checks: CheckResult[],
 24→): Map<number, CheckVerdict> {
 25→  const verdicts = new Map<number, CheckVerdict>();
 26→
 27→  for (const branch of branches) {
 28→    let worstVerdict: CheckVerdict = "PASS";
 29→
 30→    for (const check of checks) {
 31→      const branchResult = check.branchResults.find(
 32→        (r) => r.issueNumber === branch.issueNumber,
 33→      );
 34→      if (!branchResult) continue;
 35→
 36→      if (branchResult.verdict === "FAIL") {
 37→        worstVerdict = "FAIL";
 38→      } else if (branchResult.verdict === "WARN" && worstVerdict !== "FAIL") {
 39→        worstVerdict = "WARN";
 40→      }
 41→    }
 42→
 43→    verdicts.set(branch.issueNumber, worstVerdict);
 44→  }
 45→
 46→  return verdicts;
 47→}
 48→
 49→/**
 50→ * Compute batch-level verdict from per-issue verdicts
 51→ */
 52→export function computeBatchVerdict(
 53→  issueVerdicts: Map<number, CheckVerdict>,
 54→): BatchVerdict {
 55→  const verdicts = Array.from(issueVerdicts.values());
 56→
 57→  if (verdicts.some((v) => v === "FAIL")) {
 58→    return "BLOCKED";
 59→  }
 60→  if (verdicts.some((v) => v === "WARN")) {
 61→    return "NEEDS_ATTENTION";
 62→  }
 63→  return "READY";
 64→}
 65→
 66→/**
 67→ * Collect all findings from check results
 68→ */
 69→function collectFindings(checks: CheckResult[]): CheckFinding[] {
 70→  const findings: CheckFinding[] = [];
 71→  for (const check of checks) {
 72→    findings.push(...check.batchFindings);
 73→    for (const br of check.branchResults) {
 74→      findings.push(...br.findings);
 75→    }
 76→  }
 77→  return findings;
 78→}
 79→
 80→/**
 81→ * Build a MergeReport from check results
 82→ */
 83→export function buildReport(
 84→  branches: BranchInfo[],
 85→  checks: CheckResult[],
 86→  runId?: string,
 87→): MergeReport {
 88→  const issueVerdicts = computeIssueVerdicts(branches, checks);
 89→  const batchVerdict = computeBatchVerdict(issueVerdicts);
 90→  const findings = collectFindings(checks);
 91→
 92→  return {
 93→    runId,
 94→    timestamp: new Date().toISOString(),
 95→    branches,
 96→    checks,
 97→    issueVerdicts,
 98→    batchVerdict,
 99→    findings,
100→  };
101→}
102→
