  1→/**
  2→ * Merge readiness report generation (AC-5)
  3→ *
  4→ * Produces structured Markdown report with per-PR verdicts and
  5→ * batch-level verdict. Optionally posts to GitHub as PR comment.
  6→ */
  7→
  8→import { spawnSync } from "child_process";
  9→import type {
 10→  MergeReport,
 11→  CheckResult,
 12→  CheckVerdict,
 13→  BatchVerdict,
 14→  BranchInfo,
 15→  CheckFinding,
 16→} from "./types.js";
 17→
 18→/**
 19→ * Compute per-issue verdicts from check results
 20→ */
 21→export function computeIssueVerdicts(
 22→  branches: BranchInfo[],
 23→  checks: CheckResult[],
 24→): Map<number, CheckVerdict> {
 25→  const verdicts = new Map<number, CheckVerdict>();
 26→
 27→  for (const branch of branches) {
 28→    let worstVerdict: CheckVerdict = "PASS";
 29→
 30→    for (const check of checks) {
 31→      const branchResult = check.branchResults.find(
 32→        (r) => r.issueNumber === branch.issueNumber,
 33→      );
 34→      if (!branchResult) continue;
 35→
 36→      if (branchResult.verdict === "FAIL") {
 37→        worstVerdict = "FAIL";
 38→      } else if (branchResult.verdict === "WARN" && worstVerdict !== "FAIL") {
 39→        worstVerdict = "WARN";
 40→      }
 41→    }
 42→
 43→    verdicts.set(branch.issueNumber, worstVerdict);
 44→  }
 45→
 46→  return verdicts;
 47→}
 48→
 49→/**
 50→ * Compute batch-level verdict from per-issue verdicts
 51→ */
 52→export function computeBatchVerdict(
 53→  issueVerdicts: Map<number, CheckVerdict>,
 54→): BatchVerdict {
 55→  const verdicts = Array.from(issueVerdicts.values());
 56→
 57→  if (verdicts.some((v) => v === "FAIL")) {
 58→    return "BLOCKED";
 59→  }
 60→  if (verdicts.some((v) => v === "WARN")) {
 61→    return "NEEDS_ATTENTION";
 62→  }
 63→  return "READY";
 64→}
 65→
 66→/**
 67→ * Collect all findings from check results
 68→ */
 69→function collectFindings(checks: CheckResult[]): CheckFinding[] {
 70→  const findings: CheckFinding[] = [];
 71→  for (const check of checks) {
 72→    findings.push(...check.batchFindings);
 73→    for (const br of check.branchResults) {
 74→      findings.push(...br.findings);
 75→    }
 76→  }
 77→  return findings;
 78→}
 79→
 80→/**
 81→ * Build a MergeReport from check results
 82→ */
 83→export function buildReport(
 84→  branches: BranchInfo[],
 85→  checks: CheckResult[],
 86→  runId?: string,
 87→): MergeReport {
 88→  const issueVerdicts = computeIssueVerdicts(branches, checks);
 89→  const batchVerdict = computeBatchVerdict(issueVerdicts);
 90→  const findings = collectFindings(checks);
 91→
 92→  return {
 93→    runId,
 94→    timestamp: new Date().toISOString(),
 95→    branches,
 96→    checks,
 97→    issueVerdicts,
 98→    batchVerdict,
 99→    findings,
100→  };
101→}
102→
103→/**
104→ * Verdict emoji
105→ */
106→function verdictIcon(v: CheckVerdict | BatchVerdict): string {
107→  switch (v) {
108→    case "PASS":
109→    case "READY":
110→      return "\u2705";
111→    case "WARN":
112→    case "NEEDS_ATTENTION":
113→      return "\u26a0\ufe0f";
114→    case "FAIL":
115→    case "BLOCKED":
116→      return "\u274c";
117→  }
118→}
119→
120→/**
121→ * Format a MergeReport as Markdown
122→ */
123→export function formatReportMarkdown(report: MergeReport): string {
124→  const lines: string[] = [];
125→
126→  lines.push("# Merge Readiness Report");
127→  lines.push("");
128→  if (report.runId) {
129→    lines.push(`**Run:** ${report.runId}`);
130→  }
131→  lines.push(`**Generated:** ${report.timestamp}`);
132→  lines.push(
133→    `**Batch Verdict:** ${verdictIcon(report.batchVerdict)} **${report.batchVerdict}**`,
134→  );
135→  lines.push("");
136→
137→  // Per-issue verdicts table
138→  lines.push("## Per-Issue Verdicts");
139→  lines.push("");
140→  lines.push("| Issue | Title | Verdict |");
141→  lines.push("|-------|-------|---------|");
142→  for (const branch of report.branches) {
143→    const verdict = report.issueVerdicts.get(branch.issueNumber) ?? "PASS";
144→    lines.push(
145→      `| #${branch.issueNumber} | ${branch.title} | ${verdictIcon(verdict)} ${verdict} |`,
146→    );
147→  }
148→  lines.push("");
149→
150→  // Check results
151→  for (const check of report.checks) {
152→    lines.push(`## ${formatCheckName(check.name)}`);
153→    lines.push("");
154→    lines.push(
155→      `**Status:** ${check.passed ? "\u2705 Passed" : "\u274c Issues found"} (${Math.round(check.durationMs / 1000)}s)`,
156→    );
157→    lines.push("");
158→
159→    // Batch findings
160→    if (check.batchFindings.length > 0) {
161→      for (const finding of check.batchFindings) {
162→        lines.push(`- ${severityIcon(finding.severity)} ${finding.message}`);
163→      }
164→      lines.push("");
165→    }
166→
167→    // Per-branch findings (only show non-info)
168→    for (const br of check.branchResults) {
169→      const significant = br.findings.filter((f) => f.severity !== "info");
170→      if (significant.length > 0) {
171→        lines.push(`### Issue #${br.issueNumber}`);
172→        lines.push("");
173→        for (const finding of significant) {
174→          lines.push(`- ${severityIcon(finding.severity)} ${finding.message}`);
175→        }
176→        lines.push("");
177→      }
178→    }
179→  }
180→
181→  // Summary
182→  const errors = report.findings.filter((f) => f.severity === "error");
183→  const warnings = report.findings.filter((f) => f.severity === "warning");
184→  lines.push("## Summary");
185→  lines.push("");
186→  lines.push(`- **Errors:** ${errors.length}`);
187→  lines.push(`- **Warnings:** ${warnings.length}`);
188→  lines.push(`- **Issues in batch:** ${report.branches.length}`);
189→  lines.push(`- **Checks run:** ${report.checks.length}`);
190→  lines.push("");
191→
192→  return lines.join("\n");
193→}
194→
195→/**
196→ * Format a report scoped to a single issue/PR.
197→ *
198→ * Includes batch verdict header, the issue's own findings, and any
199→ * cross-issue findings that mention this issue (e.g. overlap warnings).
200→ */
201→export function formatBranchReportMarkdown(
202→  report: MergeReport,
203→  issueNumber: number,
204→): string {
205→  const lines: string[] = [];
206→  const branch = report.branches.find((b) => b.issueNumber === issueNumber);
207→  if (!branch) {
208→    return `No data for issue #${issueNumber} in this report.`;
209→  }
210→
211→  const issueVerdict = report.issueVerdicts.get(issueNumber) ?? "PASS";
212→
213→  lines.push("# Merge Readiness — Per-Issue Report");
214→  lines.push("");
215→  lines.push(`**Issue:** #${issueNumber} — ${branch.title}`);
216→  lines.push(`**Branch:** \`${branch.branch}\``);
217→  lines.push(
218→    `**Batch Verdict:** ${verdictIcon(report.batchVerdict)} **${report.batchVerdict}**`,
219→  );
220→  lines.push(
221→    `**Issue Verdict:** ${verdictIcon(issueVerdict)} **${issueVerdict}**`,
222→  );
223→  lines.push("");
224→
225→  for (const check of report.checks) {
226→    const branchResult = check.branchResults.find(
227→      (r) => r.issueNumber === issueNumber,
228→    );
229→    if (!branchResult) continue;
230→
231→    const significant = branchResult.findings.filter(
232→      (f) => f.severity !== "info",
233→    );
234→    const relevantBatch = check.batchFindings.filter(
235→      (f) =>
236→        f.issueNumber === issueNumber || f.message.includes(`#${issueNumber}`),
237→    );
238→
239→    if (significant.length === 0 && relevantBatch.length === 0) {
240→      continue;
241→    }
242→
243→    lines.push(`## ${formatCheckName(check.name)}`);
244→    lines.push("");
245→
246→    for (const finding of relevantBatch) {
247→      lines.push(`- ${severityIcon(finding.severity)} ${finding.message}`);
248→    }
249→    for (const finding of significant) {
250→      lines.push(`- ${severityIcon(finding.severity)} ${finding.message}`);
251→    }
252→    lines.push("");
253→  }
254→
255→  if (lines.length <= 7) {
256→    lines.push("All checks passed for this issue.");
257→    lines.push("");
258→  }
259→
260→  return lines.join("\n");
261→}
262→
263→function formatCheckName(name: string): string {
264→  return name
265→    .split("-")
266→    .map((w) => w.charAt(0).toUpperCase() + w.slice(1))
267→    .join(" ");
268→}
269→
270→function severityIcon(severity: string): string {
271→  switch (severity) {
272→    case "error":
273→      return "\u274c";
274→    case "warning":
275→      return "\u26a0\ufe0f";
276→    case "info":
277→      return "\u2139\ufe0f";
278→    default:
279→      return "";
280→  }
281→}
282→
283→/**
284→ * Post report to GitHub as PR comments (AC-5 --post flag)
285→ */
286→export function postReportToGitHub(report: MergeReport): void {
287→  for (const branch of report.branches) {
288→    if (!branch.prNumber) continue;
289→
290→    const markdown = formatBranchReportMarkdown(report, branch.issueNumber);
291→
292→    const result = spawnSync(
293→      "gh",
294→      ["pr", "comment", String(branch.prNumber), "--body", markdown],
295→      { stdio: "pipe", encoding: "utf-8" },
296→    );
297→
298→    if (result.status !== 0) {
299→      console.error(
300→        `Failed to post comment on PR #${branch.prNumber}: ${result.stderr}`,
301→      );
302→    }
303→  }
304→}
305→

  1→/**
  2→ * Residual pattern detection (AC-3)
  3→ *
  4→ * Extracts removed/replaced patterns from each PR's diff,
  5→ * generates literal search strings, and greps the codebase
  6→ * for remaining instances (excluding already-modified files).
  7→ */
  8→
  9→import { spawnSync } from "child_process";
 10→import type {
 11→  BranchInfo,
 12→  CheckResult,
 13→  BranchCheckResult,
 14→  CheckFinding,
 15→  ExtractedPattern,
 16→  ResidualMatch,
 17→} from "./types.js";
 18→
 19→/**
 20→ * Minimum pattern length to avoid false positives from trivial removals
 21→ */
 22→const MIN_PATTERN_LENGTH = 8;
 23→
 24→/**
 25→ * Maximum number of patterns to scan per branch (avoid explosion)
 26→ */
 27→const MAX_PATTERNS_PER_BRANCH = 50;
 28→
 29→/**
 30→ * Lines that should be ignored when extracting patterns
 31→ * (import statements, blank lines, comments, etc.)
 32→ */
 33→const IGNORE_PATTERNS = [
 34→  /^\s*$/,
 35→  /^\s*\/\//,
 36→  /^\s*\/?\*/, // Single-line comment or multi-line comment line
 37→  /^\s*import\s/,
 38→  /^\s*export\s/,
 39→  /^\s*\{?\s*\}?\s*$/,
 40→  /^\s*\)\s*;?\s*$/,
 41→  /^\s*\]\s*;?\s*$/,
 42→];
 43→
 44→/**
 45→ * Extract removed patterns from a git diff for a branch.
 46→ *
 47→ * Looks at lines removed (prefixed with -) in the diff and extracts
 48→ * significant literal strings that might indicate incomplete migration.
 49→ */
 50→export function extractPatternsFromDiff(
 51→  branch: BranchInfo,
 52→  repoRoot: string,
 53→): ExtractedPattern[] {
 54→  const diffResult = spawnSync(
 55→    "git",
 56→    [
 57→      "-C",
 58→      repoRoot,
 59→      "diff",
 60→      `origin/main...origin/${branch.branch}`,
 61→      "--unified=0",
 62→    ],
 63→    { stdio: "pipe", encoding: "utf-8" },
 64→  );
 65→
 66→  if (diffResult.status !== 0 || !diffResult.stdout) {
 67→    return [];
 68→  }
 69→
 70→  const patterns: ExtractedPattern[] = [];
 71→  const seenPatterns = new Set<string>();
 72→  let currentFile = "";
 73→
 74→  for (const line of diffResult.stdout.split("\n")) {
 75→    // Track current file from diff header
 76→    if (line.startsWith("--- a/")) {
 77→      currentFile = line.slice(6);
 78→      continue;
 79→    }
 80→
 81→    // Only look at removed lines (not additions)
 82→    if (!line.startsWith("-") || line.startsWith("---")) {
 83→      continue;
 84→    }
 85→
 86→    const content = line.slice(1).trim();
 87→
 88→    // Skip trivial lines
 89→    if (content.length < MIN_PATTERN_LENGTH) continue;
 90→    if (IGNORE_PATTERNS.some((p) => p.test(content))) continue;
 91→
 92→    // Deduplicate
 93→    if (seenPatterns.has(content)) continue;
 94→    seenPatterns.add(content);
 95→
 96→    patterns.push({
 97→      pattern: content,
 98→      sourceFile: currentFile,
 99→      issueNumber: branch.issueNumber,
100→    });
101→
102→    if (patterns.length >= MAX_PATTERNS_PER_BRANCH) break;
103→  }
104→
105→  return patterns;
106→}
107→
108→/**
109→ * Search the codebase for remaining instances of extracted patterns.
110→ *
111→ * Uses git grep for literal string matching. Excludes files that were
112→ * already modified by the branch (they've already been addressed).
113→ */
114→export function findResiduals(
115→  patterns: ExtractedPattern[],
116→  excludeFiles: string[],
117→  repoRoot: string,
118→): ResidualMatch[] {
119→  const matches: ResidualMatch[] = [];
120→  const excludeSet = new Set(excludeFiles);
121→
122→  for (const pattern of patterns) {
123→    // Use git grep for literal search (fast, respects .gitignore)
124→    const grepResult = spawnSync(
125→      "git",
126→      [
127→        "-C",
128→        repoRoot,
129→        "grep",
130→        "-n",
131→        "--fixed-strings",
132→        pattern.pattern,
133→        "--",
134→        "*.ts",
135→        "*.tsx",
136→        "*.js",
137→        "*.jsx",
138→        "*.md",
139→        "*.sh",
140→      ],
141→      { stdio: "pipe", encoding: "utf-8", timeout: 10_000 },
142→    );
143→
144→    if (grepResult.status !== 0 || !grepResult.stdout) {
145→      continue;
146→    }
147→
148→    for (const line of grepResult.stdout.split("\n")) {
149→      if (!line.trim()) continue;
150→
151→      // Format: file:line:content
152→      const colonIdx = line.indexOf(":");
153→      if (colonIdx === -1) continue;
154→
155→      const file = line.slice(0, colonIdx);
156→      const rest = line.slice(colonIdx + 1);
157→      const secondColon = rest.indexOf(":");
158→      const lineNum =
159→        secondColon > -1 ? parseInt(rest.slice(0, secondColon), 10) : 0;
160→      const content =
161→        secondColon > -1 ? rest.slice(secondColon + 1).trim() : rest.trim();
162→
163→      // Skip files that were already modified by this branch
164→      if (excludeSet.has(file)) continue;
165→
166→      // Skip test files and node_modules
167→      if (
168→        file.includes("node_modules/") ||
169→        file.includes(".test.") ||
170→        file.includes(".spec.")
171→      ) {
172→        continue;
173→      }
174→
175→      matches.push({
176→        pattern: pattern.pattern,
177→        file,
178→        line: lineNum,
179→        content: content.slice(0, 200), // Truncate long lines
180→        issueNumber: pattern.issueNumber,
181→      });
182→    }
183→  }
184→
185→  return matches;
186→}
187→
188→/**
189→ * Run residual pattern scan across all branches.
190→ */
191→export function runResidualPatternScan(
192→  branches: BranchInfo[],
193→  repoRoot: string,
194→): CheckResult {
195→  const startTime = Date.now();
196→  const branchResults: BranchCheckResult[] = [];
197→  const batchFindings: CheckFinding[] = [];
198→
199→  for (const branch of branches) {
200→    const findings: CheckFinding[] = [];
201→
202→    // Extract patterns from this branch's diff
203→    const patterns = extractPatternsFromDiff(branch, repoRoot);
204→
205→    if (patterns.length === 0) {
206→      branchResults.push({
207→        issueNumber: branch.issueNumber,
208→        verdict: "PASS",
209→        findings: [
210→          {
211→            check: "residual-pattern-scan",
212→            severity: "info",
213→            message: "No significant patterns extracted from diff",
214→            issueNumber: branch.issueNumber,
215→          },
216→        ],
217→      });
218→      continue;
219→    }
220→
221→    // Search for residuals, excluding files already modified
222→    const residuals = findResiduals(patterns, branch.filesModified, repoRoot);
223→
224→    if (residuals.length === 0) {
225→      branchResults.push({
226→        issueNumber: branch.issueNumber,
227→        verdict: "PASS",
228→        findings: [
229→          {
230→            check: "residual-pattern-scan",
231→            severity: "info",
232→            message: `Scanned ${patterns.length} patterns — no residuals found`,
233→            issueNumber: branch.issueNumber,
234→          },
235→        ],
236→      });
237→      continue;
238→    }
239→
240→    // Group residuals by pattern for cleaner reporting
241→    const byPattern = new Map<string, ResidualMatch[]>();
242→    for (const match of residuals) {
243→      const existing = byPattern.get(match.pattern) ?? [];
244→      existing.push(match);
245→      byPattern.set(match.pattern, existing);
246→    }
247→
248→    for (const [pattern, matches] of byPattern) {
249→      findings.push({
250→        check: "residual-pattern-scan",
251→        severity: "warning",
252→        message: `Pattern "${pattern.slice(0, 60)}${pattern.length > 60 ? "..." : ""}" still found in ${matches.length} file(s): ${matches.map((m) => m.file).join(", ")}`,
253→        issueNumber: branch.issueNumber,
254→      });
255→    }
256→
257→    branchResults.push({
258→      issueNumber: branch.issueNumber,
259→      verdict: "WARN",
260→      findings,
261→    });
262→
263→    // Also add to batch findings for the summary
264→    batchFindings.push({
265→      check: "residual-pattern-scan",
266→      severity: "warning",
267→      message: `Issue #${branch.issueNumber}: ${residuals.length} residual match(es) across ${byPattern.size} pattern(s)`,
268→      issueNumber: branch.issueNumber,
269→    });
270→  }
271→
272→  const hasResiduals = branchResults.some((r) => r.verdict !== "PASS");
273→  return {
274→    name: "residual-pattern-scan",
275→    passed: !hasResiduals,
276→    branchResults,
277→    batchFindings,
278→    durationMs: Date.now() - startTime,
279→  };
280→}
281→

  1→/**
  2→ * Cross-issue file overlap detection (AC-4)
  3→ *
  4→ * Compares git diff --name-only across all feature branches to detect
  5→ * when multiple PRs modify the same files. Classifies overlaps as
  6→ * "additive" (different lines) or "conflicting" (same lines).
  7→ */
  8→
  9→import { spawnSync } from "child_process";
 10→import type {
 11→  BranchInfo,
 12→  CheckResult,
 13→  BranchCheckResult,
 14→  CheckFinding,
 15→  FileOverlap,
 16→} from "./types.js";
 17→
 18→/**
 19→ * Parse git diff hunk headers to extract changed line ranges.
 20→ * Returns an array of [start, end] tuples.
 21→ */
 22→function getChangedLineRanges(
 23→  branch: string,
 24→  file: string,
 25→  repoRoot: string,
 26→): Array<[number, number]> {
 27→  const result = spawnSync(
 28→    "git",
 29→    [
 30→      "-C",
 31→      repoRoot,
 32→      "diff",
 33→      "--unified=0",
 34→      `origin/main...origin/${branch}`,
 35→      "--",
 36→      file,
 37→    ],
 38→    { stdio: "pipe", encoding: "utf-8" },
 39→  );
 40→  if (result.status !== 0 || !result.stdout) {
 41→    return [];
 42→  }
 43→  const ranges: Array<[number, number]> = [];
 44→  for (const line of result.stdout.split("\n")) {
 45→    const match = line.match(/^@@\s+[^\s]+\s+\+(\d+)(?:,(\d+))?\s+@@/);
 46→    if (match) {
 47→      const start = parseInt(match[1], 10);
 48→      const count = match[2] ? parseInt(match[2], 10) : 1;
 49→      if (count > 0) {
 50→        ranges.push([start, start + count - 1]);
 51→      }
 52→    }
 53→  }
 54→  return ranges;
 55→}
 56→
 57→/**
 58→ * Check if any ranges from two sets overlap.
 59→ */
 60→export function rangesOverlap(
 61→  a: Array<[number, number]>,
 62→  b: Array<[number, number]>,
 63→): boolean {
 64→  for (const [aStart, aEnd] of a) {
 65→    for (const [bStart, bEnd] of b) {
 66→      if (aStart <= bEnd && bStart <= aEnd) {
 67→        return true;
 68→      }
 69→    }
 70→  }
 71→  return false;
 72→}
 73→
 74→/**
 75→ * Classify overlap as "conflicting" (same lines) or "additive" (different lines).
 76→ */
 77→function classifyOverlap(
 78→  file: string,
 79→  issueBranches: Array<{ issueNumber: number; branch: string }>,
 80→  repoRoot: string,
 81→): "additive" | "conflicting" {
 82→  const rangesByIssue = issueBranches.map((b) => ({
 83→    issueNumber: b.issueNumber,
 84→    ranges: getChangedLineRanges(b.branch, file, repoRoot),
 85→  }));
 86→
 87→  for (let i = 0; i < rangesByIssue.length; i++) {
 88→    for (let j = i + 1; j < rangesByIssue.length; j++) {
 89→      if (rangesOverlap(rangesByIssue[i].ranges, rangesByIssue[j].ranges)) {
 90→        return "conflicting";
 91→      }
 92→    }
 93→  }
 94→  return "additive";
 95→}
 96→
 97→/**
 98→ * Run overlap detection across all branches.
 99→ *
100→ * Builds a map of file -> issues, then flags files with multiple modifiers.
101→ * Overlaps are classified as "additive" (different lines changed) or
102→ * "conflicting" (same lines changed) using git diff hunk analysis.
103→ */
104→export function runOverlapDetection(
105→  branches: BranchInfo[],
106→  repoRoot: string,
107→): CheckResult {
108→  const startTime = Date.now();
109→  const branchResults: BranchCheckResult[] = [];
110→  const batchFindings: CheckFinding[] = [];
111→
112→  // Build file -> issues map
113→  const fileToIssues = new Map<string, number[]>();
114→  for (const branch of branches) {
115→    for (const file of branch.filesModified) {
116→      const existing = fileToIssues.get(file) ?? [];
117→      existing.push(branch.issueNumber);
118→      fileToIssues.set(file, existing);
119→    }
120→  }
121→
122→  // Find overlapping files (modified by 2+ issues)
123→  const overlaps: FileOverlap[] = [];
124→  for (const [file, issues] of fileToIssues) {
125→    if (issues.length >= 2) {
126→      const issueBranches = issues
127→        .map((iss) => branches.find((b) => b.issueNumber === iss))
128→        .filter((b): b is BranchInfo => b !== undefined)
129→        .map((b) => ({ issueNumber: b.issueNumber, branch: b.branch }));
130→      overlaps.push({
131→        file,
132→        issues,
133→        type: classifyOverlap(file, issueBranches, repoRoot),
134→      });
135→    }
136→  }
137→
138→  // Report overlaps as batch findings
139→  if (overlaps.length === 0) {
140→    batchFindings.push({
141→      check: "overlap-detection",
142→      severity: "info",
143→      message: "No file overlaps detected across branches",
144→    });
145→  } else {
146→    for (const overlap of overlaps) {
147→      batchFindings.push({
148→        check: "overlap-detection",
149→        severity: "warning",
150→        message: `${overlap.file} modified by issues ${overlap.issues.map((i) => `#${i}`).join(", ")} (${overlap.type})`,
151→        file: overlap.file,
152→      });
153→    }
154→  }
155→
156→  // Per-branch: flag which branches participate in overlaps
157→  for (const branch of branches) {
158→    const branchOverlaps = overlaps.filter((o) =>
159→      o.issues.includes(branch.issueNumber),
160→    );
161→    const findings: CheckFinding[] = branchOverlaps.map((o) => ({
162→      check: "overlap-detection",
163→      severity: "warning" as const,
164→      message: `Overlaps with ${o.issues
165→        .filter((i) => i !== branch.issueNumber)
166→        .map((i) => `#${i}`)
167→        .join(", ")} on ${o.file}`,
168→      file: o.file,
169→      issueNumber: branch.issueNumber,
170→    }));
171→
172→    branchResults.push({
173→      issueNumber: branch.issueNumber,
174→      verdict: branchOverlaps.length > 0 ? "WARN" : "PASS",
175→      findings,
176→    });
177→  }
178→
179→  return {
180→    name: "overlap-detection",
181→    passed: overlaps.length === 0,
182→    branchResults,
183→    batchFindings,
184→    durationMs: Date.now() - startTime,
185→  };
186→}
187→

  1→/**
  2→ * Merge check orchestrator
  3→ *
  4→ * Coordinates all merge-check modules and resolves branches
  5→ * from run logs and git state.
  6→ */
  7→
  8→import * as fs from "fs";
  9→import * as path from "path";
 10→import * as os from "os";
 11→import { spawnSync } from "child_process";
 12→import {
 13→  RunLogSchema,
 14→  type RunLog,
 15→  LOG_PATHS,
 16→} from "../workflow/run-log-schema.js";
 17→import { getGitDiffStats } from "../workflow/git-diff-utils.js";
 18→import type {
 19→  BranchInfo,
 20→  CheckResult,
 21→  MergeCommandOptions,
 22→  MergeReport,
 23→} from "./types.js";
 24→import { DEFAULT_MIRROR_PAIRS } from "./types.js";
 25→import { runCombinedBranchTest } from "./combined-branch-test.js";
 26→import { runMirroringCheck } from "./mirroring-check.js";
 27→import { runOverlapDetection } from "./overlap-detection.js";
 28→import { runResidualPatternScan } from "./residual-pattern-scan.js";
 29→import {
 30→  buildReport,
 31→  formatReportMarkdown,
 32→  formatBranchReportMarkdown,
 33→  postReportToGitHub,
 34→} from "./report.js";
 35→
 36→/**
 37→ * Resolve log directory path
 38→ */
 39→function resolveLogDir(customPath?: string): string {
 40→  if (customPath) {
 41→    return customPath.replace("~", os.homedir());
 42→  }
 43→  const projectPath = LOG_PATHS.project;
 44→  if (fs.existsSync(projectPath)) {
 45→    return projectPath;
 46→  }
 47→  const userPath = LOG_PATHS.user.replace("~", os.homedir());
 48→  if (fs.existsSync(userPath)) {
 49→    return userPath;
 50→  }
 51→  return projectPath;
 52→}
 53→
 54→/**
 55→ * Find the most recent run log file
 56→ */
 57→export function findMostRecentLog(logDir: string): RunLog | null {
 58→  if (!fs.existsSync(logDir)) {
 59→    return null;
 60→  }
 61→
 62→  const files = fs
 63→    .readdirSync(logDir)
 64→    .filter((f) => f.startsWith("run-") && f.endsWith(".json"))
 65→    .sort()
 66→    .reverse();
 67→
 68→  if (files.length === 0) {
 69→    return null;
 70→  }
 71→
 72→  const content = fs.readFileSync(path.join(logDir, files[0]), "utf-8");
 73→  const parsed = RunLogSchema.safeParse(JSON.parse(content));
 74→  if (!parsed.success) {
 75→    return null;
 76→  }
 77→
 78→  return parsed.data;
 79→}
 80→
 81→/**
 82→ * Resolve branches from issue numbers.
 83→ *
 84→ * Uses git worktree list and remote branch patterns to find
 85→ * the feature branches for each issue.
 86→ */
 87→export function resolveBranches(
 88→  issueNumbers: number[],
 89→  repoRoot: string,
 90→  runLog?: RunLog | null,
 91→): BranchInfo[] {
 92→  const branches: BranchInfo[] = [];
 93→
 94→  // Get remote branches matching feature pattern
 95→  const branchResult = spawnSync(
 96→    "git",
 97→    ["-C", repoRoot, "branch", "-r", "--list", "origin/feature/*"],
 98→    { stdio: "pipe", encoding: "utf-8" },
 99→  );
100→
101→  const remoteBranches = branchResult.stdout
102→    ? branchResult.stdout
103→        .split("\n")
104→        .map((b) => b.trim().replace("origin/", ""))
105→        .filter(Boolean)
106→    : [];
107→
108→  // Also check worktrees for local-only branches
109→  const worktreeResult = spawnSync(
110→    "git",
111→    ["-C", repoRoot, "worktree", "list", "--porcelain"],
112→    { stdio: "pipe", encoding: "utf-8" },
113→  );
114→
115→  const worktreePaths = new Map<string, string>();
116→  let currentPath = "";
117→  for (const line of (worktreeResult.stdout ?? "").split("\n")) {
118→    if (line.startsWith("worktree ")) {
119→      currentPath = line.slice(9);
120→    } else if (line.startsWith("branch refs/heads/")) {
121→      const branch = line.slice(18);
122→      worktreePaths.set(branch, currentPath);
123→    }
124→  }
125→
126→  // Get run log issue info for titles
127→  const issueInfo = new Map<number, { title: string; prNumber?: number }>();
128→  if (runLog) {
129→    for (const issue of runLog.issues) {
130→      issueInfo.set(issue.issueNumber, {
131→        title: issue.title,
132→        prNumber: issue.prNumber,
133→      });
134→    }
135→  }
136→
137→  for (const issueNumber of issueNumbers) {
138→    // Find the branch for this issue
139→    const branchPattern = new RegExp(`^feature/${issueNumber}-`);
140→    const branch =
141→      remoteBranches.find((b) => branchPattern.test(b)) ??
142→      Array.from(worktreePaths.keys()).find((b) => branchPattern.test(b));
143→
144→    if (!branch) {
145→      console.error(`No branch found for issue #${issueNumber}`);
146→      continue;
147→    }
148→
149→    // Get modified files from the branch
150→    const worktreePath = worktreePaths.get(branch);
151→    let filesModified: string[] = [];
152→
153→    if (worktreePath) {
154→      // Use worktree for diff
155→      const diffStats = getGitDiffStats(worktreePath);
156→      filesModified = diffStats.filesModified;
157→    } else {
158→      // Use remote branch diff
159→      const diffResult = spawnSync(
160→        "git",
161→        [
162→          "-C",
163→          repoRoot,
164→          "diff",
165→          "--name-only",
166→          `origin/main...origin/${branch}`,
167→        ],
168→        { stdio: "pipe", encoding: "utf-8" },
169→      );
170→      filesModified = diffResult.stdout
171→        ? diffResult.stdout.split("\n").filter(Boolean)
172→        : [];
173→    }
174→
175→    const info = issueInfo.get(issueNumber);
176→    const title =
177→      info?.title ?? fetchIssueTitle(issueNumber) ?? `Issue #${issueNumber}`;
178→
179→    branches.push({
180→      issueNumber,
181→      title,
182→      branch,
183→      worktreePath,
184→      prNumber: info?.prNumber,
185→      filesModified,
186→    });
187→  }
188→
189→  return branches;
190→}
