  1→/**
  2→ * Combined branch testing (AC-1)
  3→ *
  4→ * Creates a temporary branch merging all feature branches from a run batch,
  5→ * runs npm test && npm run build on the combined state, and reports results.
  6→ */
  7→
  8→import { spawnSync } from "child_process";
  9→import type {
 10→  BranchInfo,
 11→  CheckResult,
 12→  BranchCheckResult,
 13→  CheckFinding,
 14→} from "./types.js";
 15→
 16→/**
 17→ * Result from merging a branch into the temp branch
 18→ */
 19→interface MergeAttempt {
 20→  issueNumber: number;
 21→  branch: string;
 22→  success: boolean;
 23→  conflictFiles?: string[];
 24→  error?: string;
 25→}
 26→
 27→/**
 28→ * Run a git command and return the result
 29→ */
 30→function git(
 31→  args: string[],
 32→  cwd?: string,
 33→): { ok: boolean; stdout: string; stderr: string } {
 34→  const result = spawnSync("git", args, {
 35→    cwd,
 36→    stdio: "pipe",
 37→    encoding: "utf-8",
 38→  });
 39→  return {
 40→    ok: result.status === 0,
 41→    stdout: result.stdout?.trim() ?? "",
 42→    stderr: result.stderr?.trim() ?? "",
 43→  };
 44→}
 45→
 46→/**
 47→ * Run npm command and return result
 48→ */
 49→function npm(
 50→  args: string[],
 51→  cwd?: string,
 52→): { ok: boolean; stdout: string; stderr: string } {
 53→  const result = spawnSync("npm", args, {
 54→    cwd,
 55→    stdio: "pipe",
 56→    encoding: "utf-8",
 57→    timeout: 120_000, // 2 min timeout for test/build
 58→  });
 59→  return {
 60→    ok: result.status === 0,
 61→    stdout: result.stdout?.trim() ?? "",
 62→    stderr: result.stderr?.trim() ?? "",
 63→  };
 64→}
 65→
 66→/**
 67→ * Create temp branch, merge all feature branches, run tests and build.
 68→ *
 69→ * @param branches - Feature branches to merge
 70→ * @param repoRoot - Path to the git repository root
 71→ * @returns CheckResult with combined test findings
 72→ */
 73→export function runCombinedBranchTest(
 74→  branches: BranchInfo[],
 75→  repoRoot: string,
 76→): CheckResult {
 77→  const startTime = Date.now();
 78→  const tempBranch = `merge-check/temp-${Date.now()}`;
 79→  const branchResults: BranchCheckResult[] = [];
 80→  const batchFindings: CheckFinding[] = [];
 81→  const mergeAttempts: MergeAttempt[] = [];
 82→
 83→  try {
 84→    // Fetch latest from remote
 85→    git(["fetch", "origin"], repoRoot);
 86→
 87→    // Create temp branch from main
 88→    const createResult = git(
 89→      ["checkout", "-b", tempBranch, "origin/main"],
 90→      repoRoot,
 91→    );
 92→    if (!createResult.ok) {
 93→      batchFindings.push({
 94→        check: "combined-branch-test",
 95→        severity: "error",
 96→        message: `Failed to create temp branch: ${createResult.stderr}`,
 97→      });
 98→      return buildResult(branchResults, batchFindings, startTime);
 99→    }
100→
101→    // Merge each feature branch
102→    for (const branch of branches) {
103→      const mergeResult = git(
104→        ["merge", "--no-ff", "--no-edit", `origin/${branch.branch}`],
105→        repoRoot,
106→      );
107→
108→      if (mergeResult.ok) {
109→        mergeAttempts.push({
110→          issueNumber: branch.issueNumber,
111→          branch: branch.branch,
112→          success: true,
113→        });
114→        branchResults.push({
115→          issueNumber: branch.issueNumber,
116→          verdict: "PASS",
117→          findings: [
118→            {
119→              check: "combined-branch-test",
120→              severity: "info",
121→              message: `Branch merged cleanly into combined state`,
122→              issueNumber: branch.issueNumber,
123→            },
124→          ],
125→        });
126→      } else {
127→        // Get conflicting files
128→        const conflictResult = git(
129→          ["diff", "--name-only", "--diff-filter=U"],
130→          repoRoot,
131→        );
132→        const conflictFiles = conflictResult.stdout
133→          ? conflictResult.stdout.split("\n")
134→          : [];
135→
136→        mergeAttempts.push({
137→          issueNumber: branch.issueNumber,
138→          branch: branch.branch,
139→          success: false,
140→          conflictFiles,
141→          error: mergeResult.stderr,
142→        });
143→
144→        branchResults.push({
145→          issueNumber: branch.issueNumber,
146→          verdict: "FAIL",
147→          findings: [
148→            {
149→              check: "combined-branch-test",
150→              severity: "error",
151→              message: `Merge conflict with ${conflictFiles.length} file(s): ${conflictFiles.join(", ")}`,
152→              issueNumber: branch.issueNumber,
153→            },
154→          ],
155→        });
156→
157→        // Abort the failed merge and continue
158→        git(["merge", "--abort"], repoRoot);
159→      }
160→    }
161→
162→    // If any merges failed, skip tests but report what we have
163→    const failedMerges = mergeAttempts.filter((m) => !m.success);
164→    if (failedMerges.length > 0) {
165→      batchFindings.push({
166→        check: "combined-branch-test",
167→        severity: "error",
168→        message: `${failedMerges.length}/${branches.length} branches had merge conflicts — skipping test/build`,
169→      });
170→      return buildResult(branchResults, batchFindings, startTime);
171→    }
172→
173→    // Run npm test
174→    const testResult = npm(["test"], repoRoot);
175→    if (testResult.ok) {
176→      batchFindings.push({
177→        check: "combined-branch-test",
178→        severity: "info",
179→        message: "npm test passed on combined state",
180→      });
181→    } else {
182→      batchFindings.push({
183→        check: "combined-branch-test",
184→        severity: "error",
185→        message: `npm test failed on combined state: ${testResult.stderr.slice(0, 500)}`,
186→      });
187→    }
188→
189→    // Run npm run build
190→    const buildResult2 = npm(["run", "build"], repoRoot);
191→    if (buildResult2.ok) {
192→      batchFindings.push({
193→        check: "combined-branch-test",
194→        severity: "info",
195→        message: "npm run build passed on combined state",
196→      });
197→    } else {
198→      batchFindings.push({
199→        check: "combined-branch-test",
200→        severity: "error",
201→        message: `npm run build failed on combined state: ${buildResult2.stderr.slice(0, 500)}`,
202→      });
203→    }
204→
205→    return buildResult(branchResults, batchFindings, startTime);
206→  } finally {
207→    // Clean up: delete temp branch and restore original state
208→    git(["checkout", "main"], repoRoot);
209→    git(["branch", "-D", tempBranch], repoRoot);
210→  }
211→}
