213→export function buildResult(
214→  branchResults: BranchCheckResult[],
215→  batchFindings: CheckFinding[],
216→  startTime: number,
217→): CheckResult {
218→  const hasErrors = batchFindings.some((f) => f.severity === "error");
219→  return {
220→    name: "combined-branch-test",
221→    passed: !hasErrors,
222→    branchResults,
223→    batchFindings,
224→    durationMs: Date.now() - startTime,
225→  };
226→}
