  1→/**
  2→ * Template/source mirroring check (AC-2)
  3→ *
  4→ * Detects paired directories and verifies that when a file is modified
  5→ * in one location, the corresponding file in the mirror is also modified.
  6→ */
  7→
  8→import type {
  9→  BranchInfo,
 10→  CheckResult,
 11→  BranchCheckResult,
 12→  CheckFinding,
 13→  MirrorPair,
 14→  UnmirroredChange,
 15→} from "./types.js";
 16→
 17→/**
 18→ * Check if a file path falls within any mirror pair
 19→ */
 20→function findMirrorPair(
 21→  filePath: string,
 22→  mirrorPairs: MirrorPair[],
 23→): {
 24→  pair: MirrorPair;
 25→  side: "source" | "target";
 26→  relativePath: string;
 27→} | null {
 28→  for (const pair of mirrorPairs) {
 29→    if (filePath.startsWith(pair.source + "/")) {
 30→      return {
 31→        pair,
 32→        side: "source",
 33→        relativePath: filePath.slice(pair.source.length + 1),
 34→      };
 35→    }
 36→    if (filePath.startsWith(pair.target + "/")) {
 37→      return {
 38→        pair,
 39→        side: "target",
 40→        relativePath: filePath.slice(pair.target.length + 1),
 41→      };
 42→    }
 43→  }
 44→  return null;
 45→}
 46→
 47→/**
 48→ * Get the expected mirror path for a file
 49→ */
 50→function getMirrorPath(
 51→  filePath: string,
 52→  mirrorPairs: MirrorPair[],
 53→): string | null {
 54→  const match = findMirrorPair(filePath, mirrorPairs);
 55→  if (!match) return null;
 56→
 57→  if (match.side === "source") {
 58→    return `${match.pair.target}/${match.relativePath}`;
 59→  }
 60→  return `${match.pair.source}/${match.relativePath}`;
 61→}
 62→
 63→/**
 64→ * Run mirroring check across all branches
 65→ *
 66→ * For each file modified by a branch, if it falls within a mirrored directory,
 67→ * verify that the corresponding mirror file was also modified.
 68→ */
 69→export function runMirroringCheck(
 70→  branches: BranchInfo[],
 71→  mirrorPairs: MirrorPair[],
 72→): CheckResult {
 73→  const startTime = Date.now();
 74→  const branchResults: BranchCheckResult[] = [];
 75→  const batchFindings: CheckFinding[] = [];
 76→
 77→  for (const branch of branches) {
 78→    const findings: CheckFinding[] = [];
 79→    const unmirroredChanges: UnmirroredChange[] = [];
 80→
 81→    for (const file of branch.filesModified) {
 82→      const mirrorPath = getMirrorPath(file, mirrorPairs);
 83→      if (!mirrorPath) continue;
 84→
 85→      // Check if the mirror file was also modified in this branch
 86→      const mirrorModified = branch.filesModified.includes(mirrorPath);
 87→      if (!mirrorModified) {
 88→        const match = findMirrorPair(file, mirrorPairs);
 89→        if (match) {
 90→          unmirroredChanges.push({
 91→            sourceFile: file,
 92→            targetFile: mirrorPath,
 93→            direction: match.side === "source" ? "source-only" : "target-only",
 94→            issueNumber: branch.issueNumber,
 95→          });
 96→        }
 97→      }
 98→    }
 99→
100→    if (unmirroredChanges.length > 0) {
101→      for (const change of unmirroredChanges) {
102→        findings.push({
103→          check: "mirroring",
104→          severity: "warning",
105→          message: `Modified ${change.sourceFile} but not its mirror ${change.targetFile}`,
106→          file: change.sourceFile,
107→          issueNumber: branch.issueNumber,
108→        });
109→      }
110→    }
111→
112→    branchResults.push({
113→      issueNumber: branch.issueNumber,
114→      verdict: unmirroredChanges.length > 0 ? "WARN" : "PASS",
115→      findings,
116→    });
117→  }
118→
119→  const hasWarnings = branchResults.some((r) => r.verdict !== "PASS");
120→  return {
121→    name: "mirroring",
122→    passed: !hasWarnings,
123→    branchResults,
124→    batchFindings,
125→    durationMs: Date.now() - startTime,
126→  };
127→}
