    return [];
  }

  const patterns: ExtractedPattern[] = [];
  const seenPatterns = new Set<string>();
  let currentFile = "";

  for (const line of diffResult.stdout.split("\n")) {
    // Track current file from diff header
    if (line.startsWith("--- a/")) {
      currentFile = line.slice(6);
      continue;
    }

    // Only look at removed lines (not additions)
    if (!line.startsWith("-") || line.startsWith("---")) {
      continue;
    }

    const content = line.slice(1).trim();

    // Skip trivial lines
    if (content.length < MIN_PATTERN_LENGTH) continue;
    if (IGNORE_PATTERNS.some((p) => p.test(content))) continue;

    // Deduplicate
    if (seenPatterns.has(content)) continue;
    seenPatterns.add(content);

    patterns.push({
      pattern: content,
      sourceFile: currentFile,
      issueNumber: branch.issueNumber,
    });

    if (patterns.length >= MAX_PATTERNS_PER_BRANCH) break;
  }

  return patterns;
}

/**
 * Search the codebase for remaining instances of extracted patterns.
 *
 * Uses git grep for literal string matching. Excludes files that were
 * already modified by the branch (they've already been addressed).
 */
export function findResiduals(
  patterns: ExtractedPattern[],
  excludeFiles: string[],
  repoRoot: string,
): ResidualMatch[] {
  const matches: ResidualMatch[] = [];
  const excludeSet = new Set(excludeFiles);

  for (const pattern of patterns) {
    // Use git grep for literal search (fast, respects .gitignore)
    const grepResult = spawnSync(
      "git",
      [
        "-C",
        repoRoot,
        "grep",
        "-n",
        "--fixed-strings",
        pattern.pattern,
        "--",
        "*.ts",
        "*.tsx",
        "*.js",
        "*.jsx",
        "*.md",
        "*.sh",
      ],
      { stdio: "pipe", encoding: "utf-8", timeout: 10_000 },
    );

    if (grepResult.status !== 0 || !grepResult.stdout) {
      continue;
    }

    for (const line of grepResult.stdout.split("\n")) {
      if (!line.trim()) continue;

      // Format: file:line:content
      const colonIdx = line.indexOf(":");
      if (colonIdx === -1) continue;

      const file = line.slice(0, colonIdx);
      const rest = line.slice(colonIdx + 1);
      const secondColon = rest.indexOf(":");
      const lineNum =
        secondColon > -1 ? parseInt(rest.slice(0, secondColon), 10) : 0;
      const content =
        secondColon > -1 ? rest.slice(secondColon + 1).trim() : rest.trim();

      // Skip files that were already modified by this branch
      if (excludeSet.has(file)) continue;

      // Skip test files and node_modules
      if (
        file.includes("node_modules/") ||
        file.includes(".test.") ||
        file.includes(".spec.")
      ) {
        continue;
      }

      matches.push({
        pattern: pattern.pattern,
        file,
        line: lineNum,
        content: content.slice(0, 200), // Truncate long lines
        issueNumber: pattern.issueNumber,
      });
    }
  }

  return matches;
}

/**
 * Run residual pattern scan across all branches.
 */
export function runResidualPatternScan(
  branches: BranchInfo[],
  repoRoot: string,
): CheckResult {
  const startTime = Date.now();
  const branchResults: BranchCheckResult[] = [];
  const batchFindings: CheckFinding[] = [];

  for (const branch of branches) {
    const findings: CheckFinding[] = [];

    // Extract patterns from this branch's diff
    const patterns = extractPatternsFromDiff(branch, repoRoot);

    if (patterns.length === 0) {
      branchResults.push({
        issueNumber: branch.issueNumber,
        verdict: "PASS",
        findings: [
          {
            check: "residual-pattern-scan",
            severity: "info",
            message: "No significant patterns extracted from diff",
            issueNumber: branch.issueNumber,
          },
        ],
      });
      continue;
    }

    // Search for residuals, excluding files already modified
    const residuals = findResiduals(patterns, branch.filesModified, repoRoot);

    if (residuals.length === 0) {
      branchResults.push({
        issueNumber: branch.issueNumber,
        verdict: "PASS",
        findings: [
          {
            check: "residual-pattern-scan",
            severity: "info",
            message: `Scanned ${patterns.length} patterns — no residuals found`,
            issueNumber: branch.issueNumber,
          },
        ],
      });
      continue;
    }

    // Group residuals by pattern for cleaner reporting
    const byPattern = new Map<string, ResidualMatch[]>();
    for (const match of residuals) {
      const existing = byPattern.get(match.pattern) ?? [];
      existing.push(match);
      byPattern.set(match.pattern, existing);
    }

    for (const [pattern, matches] of byPattern) {
      findings.push({
        check: "residual-pattern-scan",
        severity: "warning",
        message: `Pattern "${pattern.slice(0, 60)}${pattern.length > 60 ? "..." : ""}" still found in ${matches.length} file(s): ${matches.map((m) => m.file).join(", ")}`,
        issueNumber: branch.issueNumber,
      });
    }

    branchResults.push({
      issueNumber: branch.issueNumber,
      verdict: "WARN",
      findings,
    });

    // Also add to batch findings for the summary
    batchFindings.push({
      check: "residual-pattern-scan",
      severity: "warning",
      message: `Issue #${branch.issueNumber}: ${residuals.length} residual match(es) across ${byPattern.size} pattern(s)`,
      issueNumber: branch.issueNumber,
    });
  }

  const hasResiduals = branchResults.some((r) => r.verdict !== "PASS");
  return {
    name: "residual-pattern-scan",
    passed: !hasResiduals,
    branchResults,
    batchFindings,
    durationMs: Date.now() - startTime,
  };
}

/**
 * Cross-issue file overlap detection (AC-4)
 *
 * Compares git diff --name-only across all feature branches to detect
 * when multiple PRs modify the same files. Classifies overlaps as
 * "additive" (different lines) or "conflicting" (same lines).
 */

import { spawnSync } from "child_process";
import type {
  BranchInfo,
  CheckResult,
  BranchCheckResult,
  CheckFinding,
  FileOverlap,
} from "./types.js";
import { getBranchRef } from "./types.js";

/**
 * Parse git diff hunk headers to extract changed line ranges.
 * Returns an array of [start, end] tuples.
 */
function getChangedLineRanges(
  branch: string,
  file: string,
  repoRoot: string,
): Array<[number, number]> {
  const result = spawnSync(
    "git",
    [
      "-C",
      repoRoot,
      "diff",
      "--unified=0",
      `origin/main...origin/${branch}`,
      "--",
      file,
    ],
    { stdio: "pipe", encoding: "utf-8" },
  );
  if (result.status !== 0 || !result.stdout) {
    return [];
  }
  const ranges: Array<[number, number]> = [];
  for (const line of result.stdout.split("\n")) {
    const match = line.match(/^@@\s+[^\s]+\s+\+(\d+)(?:,(\d+))?\s+@@/);
    if (match) {
      const start = parseInt(match[1], 10);
      const count = match[2] ? parseInt(match[2], 10) : 1;
      if (count > 0) {
        ranges.push([start, start + count - 1]);
      }
    }
  }
  return ranges;
}

/**
 * Check if any ranges from two sets overlap.
 */
export function rangesOverlap(
  a: Array<[number, number]>,
  b: Array<[number, number]>,
): boolean {
  for (const [aStart, aEnd] of a) {
    for (const [bStart, bEnd] of b) {
      if (aStart <= bEnd && bStart <= aEnd) {
        return true;
      }
    }
  }
  return false;
}

/**
 * Classify overlap as "conflicting" (same lines) or "additive" (different lines).
 */
function classifyOverlap(
  file: string,
  issueBranches: Array<{ issueNumber: number; branch: string }>,
  repoRoot: string,
): "additive" | "conflicting" {
  const rangesByIssue = issueBranches.map((b) => ({
    issueNumber: b.issueNumber,
    ranges: getChangedLineRanges(b.branch, file, repoRoot),
  }));

  for (let i = 0; i < rangesByIssue.length; i++) {
    for (let j = i + 1; j < rangesByIssue.length; j++) {
      if (rangesOverlap(rangesByIssue[i].ranges, rangesByIssue[j].ranges)) {
        return "conflicting";
      }
    }
  }
  return "additive";
}

/**
 * Run overlap detection across all branches.
 *
 * Builds a map of file -> issues, then flags files with multiple modifiers.
 * Overlaps are classified as "additive" (different lines changed) or
 * "conflicting" (same lines changed) using git diff hunk analysis.
 */
export function runOverlapDetection(
  branches: BranchInfo[],
  repoRoot: string,
): CheckResult {
  const startTime = Date.now();
  const branchResults: BranchCheckResult[] = [];
  const batchFindings: CheckFinding[] = [];

  // Build file -> issues map
  const fileToIssues = new Map<string, number[]>();
  for (const branch of branches) {
    for (const file of branch.filesModified) {
      const existing = fileToIssues.get(file) ?? [];
      existing.push(branch.issueNumber);
      fileToIssues.set(file, existing);
    }
  }

  // Find overlapping files (modified by 2+ issues)
  const overlaps: FileOverlap[] = [];
  for (const [file, issues] of fileToIssues) {
    if (issues.length >= 2) {
      const issueBranches = issues
        .map((iss) => branches.find((b) => b.issueNumber === iss))
        .filter((b): b is BranchInfo => b !== undefined)
        .map((b) => ({ issueNumber: b.issueNumber, branch: b.branch }));
      overlaps.push({
        file,
        issues,
        type: classifyOverlap(file, issueBranches, repoRoot),
      });
    }
  }

  // Report overlaps as batch findings
  if (overlaps.length === 0) {
    batchFindings.push({
      check: "overlap-detection",
      severity: "info",
      message: "No file overlaps detected across branches",
    });
  } else {
    for (const overlap of overlaps) {
      batchFindings.push({
        check: "overlap-detection",
        severity: "warning",
        message: `${overlap.file} modified by issues ${overlap.issues.map((i) => `#${i}`).join(", ")} (${overlap.type})`,
        file: overlap.file,
      });
    }
  }

  // Per-branch: flag which branches participate in overlaps
  for (const branch of branches) {
    const branchOverlaps = overlaps.filter((o) =>
      o.issues.includes(branch.issueNumber),
    );
    const findings: CheckFinding[] = branchOverlaps.map((o) => ({
      check: "overlap-detection",
      severity: "warning" as const,
      message: `Overlaps with ${o.issues
        .filter((i) => i !== branch.issueNumber)
        .map((i) => `#${i}`)
        .join(", ")} on ${o.file}`,
      file: o.file,
      issueNumber: branch.issueNumber,
    }));

    branchResults.push({
      issueNumber: branch.issueNumber,
      verdict: branchOverlaps.length > 0 ? "WARN" : "PASS",
      findings,
    });
  }

  return {
    name: "overlap-detection",
    passed: overlaps.length === 0,
    branchResults,
    batchFindings,
    durationMs: Date.now() - startTime,
  };
}

