    const checks: CheckResult[] = [
      {
        name: "combined-branch-test",
        passed: false,
        branchResults: [],
        batchFindings: [
          {
            check: "combined-branch-test",
            severity: "error",
            message: "Failed to create temp branch",
          },
        ],
        durationMs: 100,
      },
    ];
    expect(computeBatchVerdict(verdicts, checks)).toBe("BLOCKED");
  });
});

